// === MOCK PERSISTENCE ===
class MockTaskPersistence: TaskPersistence {
    var savedTasks: [Task] = []
    var shouldThrowError = false

    func save(_ task: Task) throws {
        if shouldThrowError {
            throw NSError(domain: "Test", code: 1, userInfo: nil)
        }
        savedTasks.append(task)
    }

    func load() throws -> [Task] {
        if shouldThrowError {
            throw NSError(domain: "Test", code: 1, userInfo: nil)
        }
        return savedTasks
    }

    func delete(_ task: Task) throws {
        if shouldThrowError {
            throw NSError(domain: "Test", code: 1, userInfo: nil)
        }
        savedTasks.removeAll { $0.id == task.id }
    }
}

// === COMPLETE TASK MANAGER ===
class CompleteTaskManager: TaskManager {
    override func toggleCompletion(for task: Task) throws {
        guard let index = tasks.firstIndex(where: { $0.id == task.id }) else {
            throw TaskError.taskNotFound
        }

        var updatedTask = task
        updatedTask.isCompleted.toggle()
        try persistence.save(updatedTask)
        tasks[index] = updatedTask
    }

    override func tasks(for priority: Task.Priority) -> [Task] {
        return tasks.filter { $0.priority == priority }
    }

    func loadTasks() throws {
        tasks = try persistence.load()
    }

    func deleteTask(_ task: Task) throws {
        try persistence.delete(task)
        tasks.removeAll { $0.id == task.id }
    }
}

