class RealTimeEventHandler {
    private let userStore: UserStore
    private let feedStore: FeedStore
    private let notificationStore: NotificationStore

    func handleEvent(_ event: RealTimeEvent) {
        switch event.type {
        case .newPost:
            if let post = event.data as? Post {
                feedStore.addPost(post)
            }
        case .like:
            if let likeEvent = event.data as? LikeEvent {
                feedStore.updatePostLikes(postId: likeEvent.postId, 
                                        likes: likeEvent.likeCount,
                                        isLiked: likeEvent.isLikedByCurrentUser)
            }
        case .newNotification:
            if let notification = event.data as? Notification {
                notificationStore.addNotification(notification)
            }
        case .userOnline:
            if let user = event.data as? User {
                userStore.addOnlineUser(user)
            }
        }
    }
}
```

