import Combine
import SwiftUI

// === PART 1: Core Architecture ===
struct SocialMediaUser: Identifiable, Codable, Equatable {
    let id: String
    let username: String
    let displayName: String
    let profileImageURL: URL?
    let isVerified: Bool
    var isFollowing: Bool
    var followersCount: Int
    var followingCount: Int
}

struct SocialPost: Identifiable, Codable, Equatable {
    let id: String
    let author: SocialMediaUser
    let content: String
    let timestamp: Date
    var likes: Int
    var comments: Int
    var shares: Int
    var isLiked: Bool
    var isBookmarked: Bool
    let media: [Media]?

    struct Media: Codable, Equatable {
        let url: URL
        let type: String
    }
}

class SocialUserStore: ObservableObject {
    @Published var currentUser: SocialMediaUser?
    @Published var followers: [SocialMediaUser] = []
    @Published var following: [SocialMediaUser] = []
    @Published var suggestedUsers: [SocialMediaUser] = []

    private var cancellables = Set<AnyCancellable>()

    func followUser(_ userId: String) -> AnyPublisher<SocialMediaUser, Error> {
        return Future { [weak self] promise in
            DispatchQueue.main.asyncAfter(deadline: .now() + 0.3) {
                if let index = self?.suggestedUsers.firstIndex(where: { $0.id == userId }) {
                    var user = self!.suggestedUsers[index]
                    user.isFollowing = true
                    user.followersCount += 1
                    self?.suggestedUsers[index] = user

                    // Add to following list
                    if !self!.following.contains(where: { $0.id == userId }) {
                        self?.following.append(user)
                    }

                    promise(.success(user))
                } else {
                    promise(.failure(NSError(domain: "User not found", code: 404)))
                }
            }
        }
        .eraseToAnyPublisher()
    }
}

// === PART 2: Feed Management ===
class SocialFeedStore: ObservableObject {
    @Published var posts: [SocialPost] = []
    @Published var isLoading: Bool = false
    @Published var canLoadMore: Bool = true
    @Published var error: String?

    private var currentPage: Int = 0
    private let pageSize: Int = 10
    private var cancellables = Set<AnyCancellable>()

    var trendingPosts: [SocialPost] {
        posts.filter { $0.likes > 100 }
            .sorted { $0.likes > $1.likes }
            .prefix(5)
            .map { $0 }
    }

    var recentPosts: [SocialPost] {
        posts.sorted { $0.timestamp > $1.timestamp }
    }

    func loadMorePosts() -> AnyPublisher<[SocialPost], Error> {
        guard !isLoading && canLoadMore else {
            return Empty().eraseToAnyPublisher()
        }

        isLoading = true

        return Future { [weak self] promise in
            DispatchQueue.main.asyncAfter(deadline: .now() + 1.0) {
                let newPosts = self?.generateMockPosts(page: self?.currentPage ?? 0) ?? []
                promise(.success(newPosts))
            }
        }
        .handleEvents(
            receiveOutput: { [weak self] newPosts in
                self?.posts.append(contentsOf: newPosts)
                self?.currentPage += 1
                self?.canLoadMore = newPosts.count == self?.pageSize
                self?.isLoading = false
            },
            receiveCompletion: { [weak self] completion in
                if case .failure(let error) = completion {
                    self?.error = error.localizedDescription
                }
                self?.isLoading = false
            }
        )
        .eraseToAnyPublisher()
    }

    func likePost(_ postId: String) {
        if let index = posts.firstIndex(where: { $0.id == postId }) {
            var post = posts[index]
            if post.isLiked {
                post.likes -= 1
            } else {
                post.likes += 1
            }
            post.isLiked.toggle()
            posts[index] = post
        }
    }

    private func generateMockPosts(page: Int) -> [SocialPost] {
        let users = [
            SocialMediaUser(id: "1", username: "techguru", displayName: "Tech Guru", profileImageURL: nil, isVerified: true, isFollowing: true, followersCount: 50000, followingCount: 200),
            SocialMediaUser(id: "2", username: "designmaster", displayName: "Design Master", profileImageURL: nil, isVerified: true, isFollowing: false, followersCount: 25000, followingCount: 150)
        ]

        let startIndex = page * pageSize
        let endIndex = startIndex + pageSize

        return (startIndex..<endIndex).map { index in
            SocialPost(
                id: "post-\(index)",
                author: users[index % users.count],
                content: "This is post number \(index) with some engaging content. #social #media #app",
                timestamp: Date().addingTimeInterval(-Double(index) * 3600),
                likes: Int.random(in: 0...500),
                comments: Int.random(in: 0...50),
                shares: Int.random(in: 0...20),
                isLiked: Bool.random(),
                isBookmarked: Bool.random(),
                media: nil
            )
        }
    }
}

// === PART 3: User Interactions & Notifications ===
struct SocialNotification: Identifiable, Codable {
    let id: String
    let type: String
    let fromUser: SocialMediaUser
    let targetPost: SocialPost?
    let timestamp: Date
    var isRead: Bool
}

class SocialNotificationStore: ObservableObject {
    @Published var notifications: [SocialNotification] = []
    @Published var unreadCount: Int = 0

    private var cancellables = Set<AnyCancellable>()

    init() {
        // Simulate receiving notifications
        Timer.publish(every: 5.0, on: .main, in: .common)
            .autoconnect()
            .sink { [weak self] _ in
                self?.addRandomNotification()
            }
            .store(in: &cancellables)
    }

    func markAsRead(_ notificationId: String) {
        if let index = notifications.firstIndex(where: { $0.id == notificationId }) {
            notifications[index].isRead = true
            updateUnreadCount()
        }
    }

    func markAllAsRead() {
        for index in notifications.indices {
            notifications[index].isRead = true
        }
        updateUnreadCount()
    }

    private func updateUnreadCount() {
        unreadCount = notifications.filter { !$0.isRead }.count
    }

    private func addRandomNotification() {
        let mockUser = SocialMediaUser(
            id: "mock-\(UUID())",
            username: "user\(Int.random(in: 1...1000))",
            displayName: "User \(Int.random(in: 1...1000))",
            profileImageURL: nil,
            isVerified: Bool.random(),
            isFollowing: Bool.random(),
            followersCount: Int.random(in: 0...10000),
            followingCount: Int.random(in: 0...1000)
        )

        let notificationTypes = ["like", "comment", "follow", "mention"]
        let newNotification = SocialNotification(
            id: UUID().uuidString,
            type: notificationTypes.randomElement()!,
            fromUser: mockUser,
            targetPost: nil,
            timestamp: Date(),
            isRead: false
        )

        notifications.insert(newNotification, at: 0)
        updateUnreadCount()
    }
}

// === PART 4: Advanced State Patterns ===
enum SocialAction {
    case user(UserAction)
    case feed(FeedAction)
    case notification(NotificationAction)
}

enum UserAction {
    case login(SocialMediaUser)
    case logout
    case followUser(String)
    case unfollowUser(String)
}

enum FeedAction {
    case loadFeed
    case loadMore
    case likePost(String)
    case createPost(String)
}

enum NotificationAction {
    case markAsRead(String)
    case markAllAsRead
    case loadNotifications
}

class SocialAppStore: ObservableObject {
    @Published var state: SocialAppState
    private let userStore: SocialUserStore
    private let feedStore: SocialFeedStore
    private let notificationStore: SocialNotificationStore
    private var cancellables = Set<AnyCancellable>()

    init() {
        self.userStore = SocialUserStore()
        self.feedStore = SocialFeedStore()
        self.notificationStore = SocialNotificationStore()
        self.state = SocialAppState()

        setupStateBindings()
    }

    private func setupStateBindings() {
        userStore.$currentUser
            .assign(to: &state.$currentUser)

        feedStore.$posts
            .assign(to: &state.$posts)

        notificationStore.$notifications
            .assign(to: &state.$notifications)

        notificationStore.$unreadCount
            .assign(to: &state.$unreadNotificationsCount)
    }

    func dispatch(_ action: SocialAction) {
        switch action {
        case .user(let userAction):
            handleUserAction(userAction)
        case .feed(let feedAction):
            handleFeedAction(feedAction)
        case .notification(let notificationAction):
            handleNotificationAction(notificationAction)
        }
    }

    private func handleUserAction(_ action: UserAction) {
        switch action {
        case .login(let user):
            userStore.currentUser = user
        case .logout:
            userStore.currentUser = nil
            feedStore.posts = []
            notificationStore.notifications = []
        case .followUser(let userId):
            userStore.followUser(userId)
                .sink(receiveCompletion: { _ in }, receiveValue: { _ in })
                .store(in: &cancellables)
        case .unfollowUser(let userId):
            // Implementation for unfollow
            break
        }
    }

    private func handleFeedAction(_ action: FeedAction) {
        switch action {
        case .loadFeed, .loadMore:
            feedStore.loadMorePosts()
                .sink(receiveCompletion: { _ in }, receiveValue: { _ in })
                .store(in: &cancellables)
        case .likePost(let postId):
            feedStore.likePost(postId)
        case .createPost(let content):
            // Implementation for creating post
            break
        }
    }

    private func handleNotificationAction(_ action: NotificationAction) {
        switch action {
        case .markAsRead(let notificationId):
            notificationStore.markAsRead(notificationId)
        case .markAllAsRead:
            notificationStore.markAllAsRead()
        case .loadNotifications:
            // Implementation for loading notifications
            break
        }
    }
}

struct SocialAppState {
    @Published var currentUser: SocialMediaUser?
    @Published var posts: [SocialPost] = []
    @Published var notifications: [SocialNotification] = []
    @Published var unreadNotificationsCount: Int = 0
    @Published var isLoading: Bool = false
}

