class SocialCacheManager {
    private let memoryCache = NSCache<NSString, AnyObject>()
    private let userDefaults = UserDefaults.standard

    func cacheUser(_ user: SocialMediaUser) {
        memoryCache.setObject(user as AnyObject, forKey: "user-\(user.id)" as NSString)
        if let data = try? JSONEncoder().encode(user) {
            userDefaults.set(data, forKey: "user-\(user.id)")
        }
    }

    func getCachedUser(_ userId: String) -> SocialMediaUser? {
        if let cached = memoryCache.object(forKey: "user-\(userId)" as NSString) as? SocialMediaUser {
            return cached
        }

        if let data = userDefaults.data(forKey: "user-\(userId)"),
           let user = try? JSONDecoder().decode(SocialMediaUser.self, from: data) {
            memoryCache.setObject(user as AnyObject, forKey: "user-\(userId)" as NSString)
            return user
        }

        return nil
    }
}

