import UserNotifications

class AdvancedNotificationScheduler {
    private let center = UNUserNotificationCenter.current()

    // Calendar-based triggers
    func scheduleDailyReminder(at hour: Int, minute: Int) {
        var dateComponents = DateComponents()
        dateComponents.hour = hour
        dateComponents.minute = minute

        let trigger = UNCalendarNotificationTrigger(
            dateMatching: dateComponents,
            repeats: true
        )

        let content = UNMutableNotificationContent()
        content.title = "Daily Reminder"
        content.body = "Time for your daily check-in!"
        content.sound = .default

        let request = UNNotificationRequest(
            identifier: "daily-reminder",
            content: content,
            trigger: trigger
        )

        center.add(request)
    }

    // Location-based triggers
    func scheduleLocationBasedNotification() {
        let center = CLLocationCoordinate2D(latitude: 37.7749, longitude: -122.4194)
        let region = CLCircularRegion(
            center: center,
            radius: 100.0,
            identifier: "headquarters"
        )
        region.notifyOnEntry = true
        region.notifyOnExit = false

        let trigger = UNLocationNotificationTrigger(region: region, repeats: true)

        let content = UNMutableNotificationContent()
        content.title = "Welcome!"
        content.body = "You've arrived at headquarters"

        let request = UNNotificationRequest(
            identifier: "location-notification",
            content: content,
            trigger: trigger
        )

        center.add(request)
    }

    // Time interval with conditions
    func scheduleConditionalNotification() {
        let trigger = UNTimeIntervalNotificationTrigger(
            timeInterval: 3600, // 1 hour
            repeats: true
        )

        // Custom logic for conditional delivery
        let content = UNMutableNotificationContent()
        content.title = "Conditional Notification"
        content.body = "This only shows under specific conditions"
        content.userInfo = ["condition": "needs_attention"]

        let request = UNNotificationRequest(
            identifier: "conditional-notification",
            content: content,
            trigger: trigger
        )

        center.add(request)
    }
}
```

