import Vapor
import APNS

struct PushNotificationService {
    private let apns: APNSClient

    init() throws {
        // Configure APNS with authentication
        let authentication = APNSAuthentication(
            key: .private(pem: Data(/* Your key */)),
            keyIdentifier: "KEY_ID",
            teamIdentifier: "TEAM_ID"
        )

        self.apns = try APNSClient(
            configuration: .init(
                authentication: authentication,
                environment: .production
            )
        )
    }

    func sendAdvancedPush(to deviceToken: String, payload: NotificationPayload) async throws {
        let alert = APNSAlertNotification(
            alert: .init(
                title: .raw(payload.title),
                subtitle: .raw(payload.subtitle),
                body: .raw(payload.body)
            ),
            expiration: .immediately,
            priority: .immediately,
            topic: "com.yourapp.bundle",
            payload: payload.customData,
            badge: payload.badge
        )

        try await apns.sendAlertNotification(
            alert,
            to: deviceToken
        )
    }

    func sendSilentPush(to deviceToken: String, data: [String: Any]) async throws {
        let silentNotification = APNSNotification(
            expiration: .none,
            priority: .consideringDevicePower,
            topic: "com.yourapp.bundle",
            payload: data,
            badge: nil
        )

        try await apns.sendNotification(
            silentNotification,
            to: deviceToken
        )
    }
}
