## 4. Notification Service & Content Extensions (60 min)

### Notification Service Extension:
```swift
import UserNotifications

class NotificationService: UNNotificationServiceExtension {

    var contentHandler: ((UNNotificationContent) -> Void)?
    var bestAttemptContent: UNMutableNotificationContent?

    override func didReceive(_ request: UNNotificationRequest, 
                           withContentHandler contentHandler: @escaping (UNNotificationContent) -> Void) {
        self.contentHandler = contentHandler
        self.bestAttemptContent = (request.content.mutableCopy() as? UNMutableNotificationContent)

        guard let content = bestAttemptContent else {
            contentHandler(request.content)
            return
        }

        // Modify notification content before delivery
        Task {
            await processNotificationContent(content)
        }
    }

    private func processNotificationContent(_ content: UNMutableNotificationContent) async {
        // Decrypt encrypted content
        if let encryptedData = content.userInfo["encrypted_data"] as? String {
            let decryptedData = await decryptData(encryptedData)
            content.title = decryptedData.title
            content.body = decryptedData.body
        }

        // Download and attach media
        if let mediaURLString = content.userInfo["media_url"] as? String,
           let mediaURL = URL(string: mediaURLString) {
            do {
                let attachment = try await downloadAndCreateAttachment(from: mediaURL)
                content.attachments = [attachment]
            } catch {
                print("Failed to download media: " + error.localizedDescription)
            }
        }

        // Localize content
        if let localizationKey = content.userInfo["localization_key"] as? String {
            content.title = NSLocalizedString(localizationKey + "_title", comment: "")
            content.body = NSLocalizedString(localizationKey + "_body", comment: "")
        }

        contentHandler?(content)
    }

    private func downloadAndCreateAttachment(from url: URL) async throws -> UNNotificationAttachment {
        let (data, _) = try await URLSession.shared.data(from: url)

        let tempDir = FileManager.default.temporaryDirectory
        let fileURL = tempDir.appendingPathComponent(UUID().uuidString + ".tmp")

        try data.write(to: fileURL)

        return try UNNotificationAttachment(
            identifier: "media",
            url: fileURL,
            options: nil
        )
    }

    override func serviceExtensionTimeWillExpire() {
        // Clean up and call content handler with best attempt
        if let contentHandler = contentHandler, let bestAttemptContent = bestAttemptContent {
            contentHandler(bestAttemptContent)
