// Rate limiting and throttling
class NotificationThrottler {
    private var deliveryTimes: [String: Date] = [:]
    private let deliveryWindow: TimeInterval = 3600 // 1 hour
    private let maxDeliveriesPerWindow = 5

    func shouldDeliverNotification(type: String) -> Bool {
        let now = Date()
        let recentDeliveries = deliveryTimes.filter { _, timestamp in
            now.timeIntervalSince(timestamp) < deliveryWindow
        }

        let typeDeliveries = recentDeliveries.filter { key, _ in
            key.hasPrefix(type)
        }

        return typeDeliveries.count < maxDeliveriesPerWindow
    }

    func recordDelivery(type: String) {
        let key = type + "_" + UUID().uuidString
        deliveryTimes[key] = Date()

        // Clean up old entries
        cleanupOldEntries()
    }

    private func cleanupOldEntries() {
        let now = Date()
        deliveryTimes = deliveryTimes.filter { _, timestamp in
            now.timeIntervalSince(timestamp) < deliveryWindow * 2
        }
    }
}
