import UserNotifications
import UIKit
import Combine

// === ADVANCED NOTIFICATION MANAGER ===
class AdvancedNotificationManager: NSObject, ObservableObject {

    static let shared = AdvancedNotificationManager()

    private let center = UNUserNotificationCenter.current()
    private var cancellables = Set<AnyCancellable>()

    @Published var authorizationStatus: UNAuthorizationStatus = .notDetermined
    @Published var pendingNotifications: [UNNotificationRequest] = []
    @Published var deliveredNotifications: [UNNotification] = []

    private override init() {
        super.init()
        center.delegate = self
        setupObservers()
        refreshStatus()
    }

    private func setupObservers() {
        // Observe app state changes
        NotificationCenter.default.publisher(for: UIApplication.didBecomeActiveNotification)
            .sink { [weak self] _ in
                self?.refreshStatus()
            }
            .store(in: &cancellables)
    }

    func refreshStatus() {
        Task {
            let settings = await center.notificationSettings()
            await MainActor.run {
                authorizationStatus = settings.authorizationStatus
            }

            let pending = await center.pendingNotificationRequests()
            await MainActor.run {
                pendingNotifications = pending
            }

            let delivered = await center.deliveredNotifications()
            await MainActor.run {
                deliveredNotifications = delivered
            }
        }
    }

    // === PERMISSION MANAGEMENT ===
    func requestFullAuthorization() async throws -> Bool {
        let options: UNAuthorizationOptions = [
            .alert,
            .badge,
            .sound,
            .carPlay,
            .criticalAlert,
            .providesAppNotificationSettings,
            .provisional
        ]

        let granted = try await center.requestAuthorization(options: options)
        await MainActor.run {
            authorizationStatus = granted ? .authorized : .denied
        }

        if granted {
            registerCustomCategories()
        }

        return granted
    }

    func requestCriticalAlertAuthorization() async throws -> Bool {
        let options: UNAuthorizationOptions = [.criticalAlert]
        return try await center.requestAuthorization(options: options)
    }

    // === CUSTOM CATEGORIES & ACTIONS ===
    private func registerCustomCategories() {
        // Message category with reply
        let replyAction = UNTextInputNotificationAction(
            identifier: "REPLY_ACTION",
            title: "Reply",
            options: [.foreground],
            textInputButtonTitle: "Send",
            textInputPlaceholder: "Type your message..."
        )

        let messageCategory = UNNotificationCategory(
            identifier: "MESSAGE_CATEGORY",
            actions: [replyAction],
            intentIdentifiers: [],
            options: [.customDismissAction]
        )

        // Media category with actions
        let saveAction = UNNotificationAction(
            identifier: "SAVE_MEDIA",
            title: "Save",
            options: [.authenticationRequired]
        )

        let shareAction = UNNotificationAction(
            identifier: "SHARE_MEDIA",
            title: "Share",
            options: [.foreground]
        )

        let mediaCategory = UNNotificationCategory(
            identifier: "MEDIA_CATEGORY",
            actions: [saveAction, shareAction],
            intentIdentifiers: [],
            options: [.hiddenPreviewsShowTitle]
        )

        center.setNotificationCategories([messageCategory, mediaCategory])
    }

    // === ADVANCED SCHEDULING ===
    func scheduleConditionalNotification(
        _ condition: @escaping () async -> Bool,
        content: UNNotificationContent,
        retryInterval: TimeInterval = 3600
    ) async {
        guard await condition() else {
            // Schedule retry
            let trigger = UNTimeIntervalNotificationTrigger(
                timeInterval: retryInterval,
                repeats: false
            )

            let retryContent = content.mutableCopy() as! UNMutableNotificationContent
            retryContent.body = "Conditional: " + content.body

            let request = UNNotificationRequest(
                identifier: "conditional-retry-" + UUID().uuidString,
                content: retryContent,
                trigger: trigger
            )

            try? await center.add(request)
            return
        }

        let request = UNNotificationRequest(
            identifier: "conditional-" + UUID().uuidString,
            content: content,
            trigger: nil // Deliver immediately
        )

        try? await center.add(request)
    }

    func scheduleRecurringNotification(
        with content: UNNotificationContent,
        frequency: RecurringFrequency,
        startDate: Date = Date()
    ) {
        let calendar = Calendar.current
        var dateComponents = calendar.dateComponents([.hour, .minute], from: startDate)

        switch frequency {
        case .daily:
            break // Already have hour and minute
        case .weekly(let weekday):
            dateComponents.weekday = weekday.rawValue
        case .monthly(let day):
            dateComponents.day = day
        case .yearly(let month, let day):
            dateComponents.month = month
            dateComponents.day = day
        }

        let trigger = UNCalendarNotificationTrigger(
            dateMatching: dateComponents,
            repeats: true
        )

        let request = UNNotificationRequest(
            identifier: "recurring-" + UUID().uuidString,
            content: content,
            trigger: trigger
        )

        Task {
            try? await center.add(request)
        }
    }

    // === NOTIFICATION GROUPS & THREADS ===
    func scheduleThreadedNotification(
        threadId: String,
        messages: [Message],
        summaryFormat: String = "%u new messages"
    ) {
        let content = UNMutableNotificationContent()

        if let latestMessage = messages.last {
            content.title = latestMessage.sender
            content.body = latestMessage.text
        }

        content.threadIdentifier = threadId
        content.summaryArgument = String(messages.count)
        content.summaryArgumentCount = messages.count

        if let summaryFormat = summaryFormat as? String {
            // Custom summary formatting
            content.categoryIdentifier = "THREADED_CATEGORY"
        }

        let request = UNNotificationRequest(
            identifier: "threaded-" + threadId,
            content: content,
            trigger: nil
        )

        Task {
            try? await center.add(request)
        }
    }

    // === BATCH OPERATIONS ===
    func scheduleBatchNotifications(_ notifications: [BatchNotification]) async {
        await withThrowingTaskGroup(of: Void.self) { group in
            for notification in notifications {
                group.addTask {
                    let request = UNNotificationRequest(
                        identifier: notification.id,
                        content: notification.content,
                        trigger: notification.trigger
                    )
                    try await self.center.add(request)
                }
            }

            // Wait for all to complete
            for try await _ in group {}
        }

        await refreshStatus()
    }

    func removeNotifications(withIdentifiers identifiers: [String]) async {
        await center.removePendingNotificationRequests(withIdentifiers: identifiers)
        await center.removeDeliveredNotifications(withIdentifiers: identifiers)
        await refreshStatus()
    }

    func clearAllNotifications() async {
        center.removeAllPendingNotificationRequests()
        center.removeAllDeliveredNotifications()
        await refreshStatus()
    }
}

