class SecureMediaNotificationService: UNNotificationServiceExtension {

    override func didReceive(_ request: UNNotificationRequest, 
                           withContentHandler contentHandler: @escaping (UNNotificationContent) -> Void) {

        let content = request.content.mutableCopy() as! UNMutableNotificationContent

        Task {
            // Handle encrypted media
            if let encryptedMedia = content.userInfo["encrypted_media"] as? [String: Any] {
                await processEncryptedMedia(encryptedMedia, for: content)
            }

            // Download and attach media
            if let mediaURL = content.userInfo["media_url"] as? String {
                await attachMedia(from: mediaURL, to: content)
            }

            contentHandler(content)
        }
    }

    private func processEncryptedMedia(_ mediaData: [String: Any], for content: UNMutableNotificationContent) async {
        guard let encryptedData = mediaData["data"] as? Data,
              let key = mediaData["key"] as? String else { return }

        do {
            let decryptedData = try await decryptMedia(encryptedData, with: key)
            let tempURL = FileManager.default.temporaryDirectory
                .appendingPathComponent(UUID().uuidString + ".media")

            try decryptedData.write(to: tempURL)

            let attachment = try UNNotificationAttachment(
                identifier: "secure-media",
                url: tempURL
            )

            content.attachments = [attachment]
        } catch {
            print("Failed to decrypt media: " + error.localizedDescription)
        }
    }

    private func decryptMedia(_ data: Data, with key: String) async throws -> Data {
