// === PART 3: Smart Notification Delivery ===
class SmartDeliveryManager {

    private let center = UNUserNotificationCenter.current()
    private let userActivityTracker: UserActivityTracker
    private let rateLimiter: NotificationRateLimiter

    init() {
        self.userActivityTracker = UserActivityTracker()
        self.rateLimiter = NotificationRateLimiter()
    }

    func scheduleOptimalNotification(_ content: UNNotificationContent) async {
        // Check rate limiting
        guard rateLimiter.canDeliverNotification(type: content.categoryIdentifier) else {
            print("Rate limited: " + content.categoryIdentifier)
            return
        }

        // Check user activity
        let userStatus = await userActivityTracker.currentUserStatus()
        let optimalTime = calculateOptimalDeliveryTime(for: userStatus)

        let trigger = UNTimeIntervalNotificationTrigger(
            timeInterval: optimalTime.timeIntervalSinceNow,
            repeats: false
        )

        let request = UNNotificationRequest(
            identifier: UUID().uuidString,
            content: content,
            trigger: trigger
        )

        try? await center.add(request)
        rateLimiter.recordDelivery(type: content.categoryIdentifier)
    }

    private func calculateOptimalDeliveryTime(for userStatus: UserStatus) -> Date {
        switch userStatus {
        case .active:
            return Date().addingTimeInterval(300) // 5 minutes
        case .idle:
            return Date().addingTimeInterval(60) // 1 minute
        case .inactive:
            return Date() // Immediate
        case .sleeping:
            return Calendar.current.nextDate(
                after: Date(),
                matching: DateComponents(hour: 9),
                matchingPolicy: .nextTime
            ) ?? Date().addingTimeInterval(3600) // Next 9 AM
        }
    }
}

// === PART 4: Server Integration ===
class PushNotificationService {

    private let serverURL: URL
    private let authManager: AuthManager

    func registerDeviceForPush(token: Data) async throws {
        let deviceToken = token.map { String(format: "%02.2hhx", $0) }.joined()

        let registration = DeviceRegistration(
            token: deviceToken,
            platform: "ios",
            appVersion: Bundle.main.version,
            preferences: await loadUserPreferences()
        )

        try await sendRegistrationToServer(registration)
    }

    private func sendRegistrationToServer(_ registration: DeviceRegistration) async throws {
        var request = URLRequest(url: serverURL.appendingPathComponent("devices/register"))
        request.httpMethod = "POST"
        request.setValue("application/json", forHTTPHeaderField: "Content-Type")
        request.setValue("Bearer " + authManager.authToken, forHTTPHeaderField: "Authorization")

        let encoder = JSONEncoder()
        request.httpBody = try encoder.encode(registration)

        let (_, response) = try await URLSession.shared.data(for: request)

