### Geofence State Machine:
```swift
class GeofenceStateMachine {
    enum State {
        case outsideAllRegions
        case insideRegion(String)
        case transitioning
    }

    private(set) var currentState: State = .outsideAllRegions
    private var enteredRegions: Set<String> = []

    func handleRegionEntry(_ identifier: String) {
        enteredRegions.insert(identifier)

        if case .outsideAllRegions = currentState {
            currentState = .insideRegion(identifier)
        } else {
            currentState = .transitioning
        }
    }

    func handleRegionExit(_ identifier: String) {
        enteredRegions.remove(identifier)

        if enteredRegions.isEmpty {
            currentState = .outsideAllRegions
        } else if case .insideRegion(let current) = currentState, current == identifier {
            currentState = .insideRegion(enteredRegions.first!)
        }
    }
}
