    @Published var locationHistory: [CLLocation] = []
    @Published var geofenceEvents: [GeofenceEvent] = []
    @Published var analytics: [String: GeofenceAnalytics] = [:]

    // MARK: - Private Properties
    private let geofenceLimit = 20
    private var backgroundTask: UIBackgroundTaskIdentifier?

    override init() {
        authorizationStatus = locationManager.authorizationStatus
        super.init()

        setupLocationManager()
        setupAppStateObservers()
        loadPersistedData()
    }

    // MARK: - Setup
    private func setupLocationManager() {
        locationManager.delegate = self
        locationManager.desiredAccuracy = kCLLocationAccuracyBest
        locationManager.distanceFilter = 10.0
        locationManager.allowsBackgroundLocationUpdates = true
        locationManager.pausesLocationUpdatesAutomatically = true
        locationManager.activityType = .other
    }

    private func setupAppStateObservers() {
        NotificationCenter.default.publisher(for: UIApplication.didEnterBackgroundNotification)
            .sink { [weak self] _ in self?.appDidEnterBackground() }
            .store(in: &cancellables)

        NotificationCenter.default.publisher(for: UIApplication.willEnterForegroundNotification)
            .sink { [weak self] _ in self?.appWillEnterForeground() }
            .store(in: &cancellables)
    }

    // MARK: - Authorization
    func requestWhenInUseAuthorization() {
        locationManager.requestWhenInUseAuthorization()
    }

    func requestAlwaysAuthorization() {
        locationManager.requestAlwaysAuthorization()
    }

    // MARK: - Location Updates
    func startLocationUpdates() {
        guard authorizationStatus == .authorizedWhenInUse || 
              authorizationStatus == .authorizedAlways else {
            return
        }
        locationManager.startUpdatingLocation()
    }

    func stopLocationUpdates() {
        locationManager.stopUpdatingLocation()
    }

    func startSignificantLocationChanges() {
        locationManager.startMonitoringSignificantLocationChanges()
    }

    // MARK: - Geofencing
    func addGeofence(center: CLLocationCoordinate2D, 
                    radius: CLLocationDistance, 
                    identifier: String) -> Bool {

        guard monitoredRegions.count < geofenceLimit else {
            print("Geofence limit reached")
            return false
        }

        guard CLLocationManager.isMonitoringAvailable(for: CLCircularRegion.self) else {
            print("Geofencing not available")
            return false
        }

        let region = CLCircularRegion(center: center, radius: radius, identifier: identifier)
        region.notifyOnEntry = true
        region.notifyOnExit = true

        locationManager.startMonitoring(for: region)
        monitoredRegions.insert(region)
        persistMonitoredRegions()

        return true
    }

    func removeGeofence(identifier: String) {
        if let region = monitoredRegions.first(where: { $0.identifier == identifier }) {
            locationManager.stopMonitoring(for: region)
            monitoredRegions.remove(region)
            persistMonitoredRegions()
        }
    }

    // MARK: - Background Optimization
    private func appDidEnterBackground() {
        // Reduce accuracy in background
        locationManager.desiredAccuracy = kCLLocationAccuracyHundredMeters
        locationManager.distanceFilter = 50.0

        // Start background task
        backgroundTask = UIApplication.shared.beginBackgroundTask {
            self.endBackgroundTask()
        }
    }

    private func appWillEnterForeground() {
        // Restore accuracy
        locationManager.desiredAccuracy = kCLLocationAccuracyBest
        locationManager.distanceFilter = 10.0
        endBackgroundTask()
    }

    private func endBackgroundTask() {
        if let task = backgroundTask {
            UIApplication.shared.endBackgroundTask(task)
            backgroundTask = nil
        }
    }

    // MARK: - Data Persistence
    private func persistMonitoredRegions() {
        let regionData = monitoredRegions.map { region -> [String: Any] in
            if let circularRegion = region as? CLCircularRegion {
                return [
                    "identifier": circularRegion.identifier,
                    "latitude": circularRegion.center.latitude,
                    "longitude": circularRegion.center.longitude,
                    "radius": circularRegion.radius
                ]
            }
            return [:]
        }

        UserDefaults.standard.set(regionData, forKey: "monitoredRegions")
    }

    private func loadPersistedData() {
        guard let regionData = UserDefaults.standard.array(forKey: "monitoredRegions") as? [[String: Any]] else {
            return
        }

        for data in regionData {
            guard let identifier = data["identifier"] as? String,
                  let latitude = data["latitude"] as? CLLocationDegrees,
                  let longitude = data["longitude"] as? CLLocationDegrees,
                  let radius = data["radius"] as? CLLocationDistance else {
                continue
            }

            let center = CLLocationCoordinate2D(latitude: latitude, longitude: longitude)
            addGeofence(center: center, radius: radius, identifier: identifier)
        }
    }

    // MARK: - Analytics
    private func recordGeofenceEvent(_ event: GeofenceEvent) {
        geofenceEvents.append(event)

        var analytic = analytics[event.regionIdentifier] ?? GeofenceAnalytics(identifier: event.regionIdentifier)

        switch event.type {
        case .entry:
            analytic.entryCount += 1
            analytic.lastEntryDate = event.timestamp
        case .exit:
            analytic.exitCount += 1
            analytic.lastExitDate = event.timestamp

            if let entryDate = analytic.lastEntryDate {
                let duration = event.timestamp.timeIntervalSince(entryDate)
                analytic.totalTimeInside += duration
                analytic.averageStayDuration = analytic.totalTimeInside / Double(analytic.exitCount)
            }
        }

        analytics[event.regionIdentifier] = analytic
    }

    private func scheduleNotification(for event: GeofenceEvent) {
        let content = UNMutableNotificationContent()

        switch event.type {
        case .entry:
            content.title = "Location Reminder"
            content.body = "You entered: " + event.regionIdentifier
        case .exit:
            content.title = "Location Reminder"
            content.body = "You left: " + event.regionIdentifier
        }

        content.sound = .default

        let request = UNNotificationRequest(
            identifier: UUID().uuidString,
            content: content,
            trigger: nil
        )

        UNUserNotificationCenter.current().add(request)
    }
}

// MARK: - CLLocationManagerDelegate
extension CompleteLocationManager: CLLocationManagerDelegate {

    func locationManagerDidChangeAuthorization(_ manager: CLLocationManager) {
        authorizationStatus = manager.authorizationStatus
    }

    func locationManager(_ manager: CLLocationManager, didUpdateLocations locations: [CLLocation]) {
        guard let location = locations.last else { return }

        // Filter by accuracy
        guard location.horizontalAccuracy >= 0 && location.horizontalAccuracy < 100 else { return }

        currentLocation = location
        locationHistory.append(location)

        // Keep history manageable
        if locationHistory.count > 1000 {
            locationHistory.removeFirst(500)
        }
    }

    func locationManager(_ manager: CLLocationManager, didFailWithError error: Error) {
        print("Location error: " + error.localizedDescription)
    }

    func locationManager(_ manager: CLLocationManager, didEnterRegion region: CLRegion) {
        guard let region = region as? CLCircularRegion else { return }

        let event = GeofenceEvent(regionIdentifier: region.identifier, type: .entry, timestamp: Date())
        recordGeofenceEvent(event)
        scheduleNotification(for: event)
    }

    func locationManager(_ manager: CLLocationManager, didExitRegion region: CLRegion) {
        guard let region = region as? CLCircularRegion else { return }

        let event = GeofenceEvent(regionIdentifier: region.identifier, type: .exit, timestamp: Date())
        recordGeofenceEvent(event)
        scheduleNotification(for: event)
    }

    func locationManager(_ manager: CLLocationManager, monitoringDidFailFor region: CLRegion?, withError error: Error) {
        print("Monitoring failed for region: " + (region?.identifier ?? "unknown"))
    }
}

// MARK: - Data Models
struct GeofenceEvent {
    let regionIdentifier: String
    let type: GeofenceEventType
    let timestamp: Date
}

enum GeofenceEventType {
    case entry, exit
}

struct GeofenceAnalytics {
    let identifier: String
    var entryCount: Int = 0
    var exitCount: Int = 0
    var totalTimeInside: TimeInterval = 0
    var averageStayDuration: TimeInterval = 0
    var lastEntryDate: Date?
    var lastExitDate: Date?
}

