    func fetchSmartAlbums() -> [PHAssetCollection] {
        let smartAlbumTypes: [PHAssetCollectionSubtype] = [
            .smartAlbumFavorites,
            .smartAlbumRecentlyAdded,
            .smartAlbumSelfPortraits,
            .smartAlbumScreenshots,
            .smartAlbumBursts
        ]

