        return collections
    }

    func fetchAssetsWithFilters() -> PHFetchResult<PHAsset> {
        let options = PHFetchOptions()

        // Filter predicates
        let creationDatePredicate = NSPredicate(format: "creationDate > %@", Date().addingTimeInterval(-7*24*60*60) as CVarArg)
        let mediaTypePredicate = NSPredicate(format: "mediaType = %d", PHAssetMediaType.image.rawValue)

        options.predicate = NSCompoundPredicate(andPredicateWithSubpredicates: [
            creationDatePredicate,
            mediaTypePredicate
        ])

        options.sortDescriptors = [NSSortDescriptor(key: "creationDate", ascending: false)]

        return PHAsset.fetchAssets(with: options)
    }
