class RealTimeImageProcessor {
    private let context = CIContext()
    private var currentFilter: CIFilter?

    func processImage(_ image: UIImage, with filter: CIFilter) -> UIImage? {
        guard let ciImage = CIImage(image: image) else { return nil }

        filter.setValue(ciImage, forKey: kCIInputImageKey)

        guard let outputImage = filter.outputImage,
              let cgImage = context.createCGImage(outputImage, from: outputImage.extent) else {
            return nil
        }

        return UIImage(cgImage: cgImage)
    }

    // Real-time filter application
    func applyFilterToLivePreview(pixelBuffer: CVPixelBuffer, filter: CIFilter) -> CVPixelBuffer? {
        let ciImage = CIImage(cvPixelBuffer: pixelBuffer)
        filter.setValue(ciImage, forKey: kCIInputImageKey)

        guard let outputImage = filter.outputImage else { return nil }

        var outputPixelBuffer: CVPixelBuffer?

        // Reuse the input pixel buffer for output
        CVPixelBufferCreate(nil, 
                           CVPixelBufferGetWidth(pixelBuffer),
                           CVPixelBufferGetHeight(pixelBuffer),
                           CVPixelBufferGetPixelFormatType(pixelBuffer),
                           nil,
                           &outputPixelBuffer)

        if let outputPixelBuffer = outputPixelBuffer {
            context.render(outputImage, to: outputPixelBuffer)
        }

        return outputPixelBuffer
    }
}
