    private var thumbnailCache: [String: UIImage] = [:]

    func generateThumbnail(for asset: PHAsset, targetSize: CGSize, completion: @escaping (UIImage?) -> Void) {
        let options = PHImageRequestOptions()
        options.deliveryMode = .highQualityFormat
        options.resizeMode = .fast
        options.isSynchronous = false

        // Check cache first
        if let cachedThumbnail = thumbnailCache[asset.localIdentifier] {
            completion(cachedThumbnail)
            return
        }

        imageManager.requestImage(for: asset, 
                                 targetSize: targetSize, 
                                 contentMode: .aspectFill, 
                                 options: options) { [weak self] image, info in
            if let image = image {
                // Cache the thumbnail
                self?.thumbnailCache[asset.localIdentifier] = image
            }
            completion(image)
        }
    }

