class MemoryEfficientImageLoader {
    private let imageManager = PHImageManager.default()
    private var requestIDs: [PHImageRequestID] = []

    func loadHighQualityImage(for asset: PHAsset, completion: @escaping (UIImage?) -> Void) {
        let options = PHImageRequestOptions()
        options.version = .current
        options.deliveryMode = .highQualityFormat
        options.isNetworkAccessAllowed = true

        let targetSize = calculateOptimalSize(for: asset)

        let requestID = imageManager.requestImage(for: asset, 
                                                 targetSize: targetSize, 
                                                 contentMode: .aspectFit, 
                                                 options: options) { image, info in
            if let isDegraded = info?[PHImageResultIsDegradedKey] as? Bool, !isDegraded {
                completion(image)
            }
        }

        requestIDs.append(requestID)
    }

    private func calculateOptimalSize(for asset: PHAsset) -> CGSize {
        let scale = UIScreen.main.scale
        let maxDimension: CGFloat = 2048 // Prevent loading massive images

        let assetRatio = CGFloat(asset.pixelWidth) / CGFloat(asset.pixelHeight)

        if assetRatio > 1 {
            // Landscape
            return CGSize(width: maxDimension, height: maxDimension / assetRatio)
        } else {
            // Portrait or square
            return CGSize(width: maxDimension * assetRatio, height: maxDimension)
        }
    }

    func cancelAllRequests() {
        requestIDs.forEach { imageManager.cancelImageRequest($0) }
        requestIDs.removeAll()
    }
}
