        return false
    }

    // MARK: - Camera Controls
    private func switchCamera() {
        sessionQueue.async {
            let currentPosition = self.videoDeviceInput.device.position
            let newPosition: AVCaptureDevice.Position = currentPosition == .back ? .front : .back

            guard let videoDevice = AVCaptureDevice.default(.builtInWideAngleCamera, for: .video, position: newPosition) else {
                return
            }

            do {
                let newVideoInput = try AVCaptureDeviceInput(device: videoDevice)

                self.captureSession.beginConfiguration()
                self.captureSession.removeInput(self.videoDeviceInput)

                if self.captureSession.canAddInput(newVideoInput) {
                    self.captureSession.addInput(newVideoInput)
                    self.videoDeviceInput = newVideoInput
                } else {
                    self.captureSession.addInput(self.videoDeviceInput)
                }

                self.captureSession.commitConfiguration()
            } catch {
                print("Error switching camera: \(error)")
            }
        }
    }

    private func setZoom(_ zoomFactor: CGFloat) {
        sessionQueue.async {
            guard let device = self.videoDeviceInput.device else { return }

            do {
                try device.lockForConfiguration()
                device.videoZoomFactor = zoomFactor
                device.unlockForConfiguration()
            } catch {
                print("Error setting zoom: \(error)")
            }
        }
    }

    // MARK: - Photo Capture
    private func capturePhoto() {
        sessionQueue.async {
            let photoSettings = AVCapturePhotoSettings()

            // Configure photo settings for best quality
            if self.photoOutput.availablePhotoCodecTypes.contains(.hevc) {
                photoSettings = AVCapturePhotoSettings(format: [AVVideoCodecKey: AVVideoCodecType.hevc])
            }

            photoSettings.isHighResolutionPhotoEnabled = true
            photoSettings.flashMode = .auto

            // Enable depth data if available
            if self.photoOutput.isDepthDataDeliverySupported {
                photoSettings.isDepthDataDeliveryEnabled = true
            }

            // Enable Live Photo if available
            if self.photoOutput.isLivePhotoCaptureSupported {
                let livePhotoMovieFileName = NSUUID().uuidString
                let livePhotoMovieFilePath = (NSTemporaryDirectory() as NSString).appendingPathComponent((livePhotoMovieFileName as NSString).appendingPathExtension("mov")!)
                photoSettings.livePhotoMovieFileURL = URL(fileURLWithPath: livePhotoMovieFilePath)
            }

            self.photoOutput.capturePhoto(with: photoSettings, delegate: self)
        }
    }
}

