class LiveFilterProcessor {
    private let context = CIContext()
    private var currentFilterChain: [CIFilter] = []

    func processVideoFrame(_ pixelBuffer: CVPixelBuffer) -> CVPixelBuffer? {
        let ciImage = CIImage(cvPixelBuffer: pixelBuffer)
        var currentImage = ciImage

        // Apply filter chain
        for filter in currentFilterChain {
            filter.setValue(currentImage, forKey: kCIInputImageKey)
            if let outputImage = filter.outputImage {
                currentImage = outputImage
            }
        }

        // Render back to pixel buffer
        var outputBuffer: CVPixelBuffer?
        let status = CVPixelBufferCreate(nil, 
                                        CVPixelBufferGetWidth(pixelBuffer),
                                        CVPixelBufferGetHeight(pixelBuffer),
                                        CVPixelBufferGetPixelFormatType(pixelBuffer),
                                        nil,
                                        &outputBuffer)

        guard status == kCVReturnSuccess, let outputBuffer = outputBuffer else {
            return nil
        }

        context.render(currentImage, to: outputBuffer)
        return outputBuffer
    }

