    func mapView(_ mapView: MKMapView, viewFor annotation: MKAnnotation) -> MKAnnotationView? {
        if annotation is MKUserLocation {
            return nil
        }

        if let cluster = annotation as? MKClusterAnnotation {
            return ClusterAnnotationView(annotation: cluster, reuseIdentifier: "cluster")
        }

        if let customAnnotation = annotation as? CustomPointAnnotation {
            let view = mapView.dequeueReusableAnnotationView(withIdentifier: CustomAnnotationView.reuseIdentifier, for: customAnnotation) as? CustomAnnotationView
            return view ?? CustomAnnotationView(annotation: customAnnotation, reuseIdentifier: CustomAnnotationView.reuseIdentifier)
        }

        return nil
    }

    func mapView(_ mapView: MKMapView, clusterAnnotationForMemberAnnotations memberAnnotations: [MKAnnotation]) -> MKClusterAnnotation {
        return MKClusterAnnotation(memberAnnotations: memberAnnotations)
