// Custom Circle Overlay
class CustomCircleOverlay: NSObject, MKOverlay {
    let coordinate: CLLocationCoordinate2D
    let boundingMapRect: MKMapRect
    let radius: CLLocationDistance

    init(center: CLLocationCoordinate2D, radius: CLLocationDistance) {
        self.coordinate = center
        self.radius = radius

        // Calculate bounding map rect
        let region = MKCoordinateRegion(center: center, latitudinalMeters: radius * 2, longitudinalMeters: radius * 2)
        self.boundingMapRect = MKMapRect(
            origin: MKMapPoint(region.center),
            size: MKMapSize(width: region.span.longitudeDelta, height: region.span.latitudeDelta)
        )
    }
}

// Custom Overlay Renderer
class CustomCircleRenderer: MKOverlayRenderer {
    override func draw(_ mapRect: MKMapRect, zoomScale: MKZoomScale, in context: CGContext) {
        guard let circleOverlay = overlay as? CustomCircleOverlay else { return }

        // Set drawing properties
        context.setLineWidth(2.0 / zoomScale)
        context.setStrokeColor(UIColor.systemBlue.cgColor)
        context.setFillColor(UIColor.systemBlue.withAlphaComponent(0.3).cgColor)

        // Create circle path
        let centerPoint = point(for: circleOverlay.coordinate)
        let radiusInPoints = metersToPoints(meters: circleOverlay.radius, zoomScale: zoomScale)

        let circlePath = UIBezierPath(
            ovalIn: CGRect(
                x: centerPoint.x - radiusInPoints,
                y: centerPoint.y - radiusInPoints,
                width: radiusInPoints * 2,
                height: radiusInPoints * 2
            )
        )

        // Draw circle
        context.addPath(circlePath.cgPath)
        context.drawPath(using: .fillStroke)
    }

    private func metersToPoints(meters: CLLocationDistance, zoomScale: MKZoomScale) -> CGFloat {
        let metersPerPoint = MKMetersPerMapPointAtLatitude((overlay as? CustomCircleOverlay)?.coordinate.latitude ?? 0)
        return CGFloat(meters / metersPerPoint) * CGFloat(zoomScale)
    }
}

