// Heat Map Style Gradient Overlay
class GradientOverlayRenderer: MKOverlayRenderer {
    private let gradientColors: [UIColor]
    private let gradientLocations: [CGFloat]

    init(overlay: MKOverlay, colors: [UIColor], locations: [CGFloat]) {
        self.gradientColors = colors
        self.gradientLocations = locations
        super.init(overlay: overlay)
    }

    override func draw(_ mapRect: MKMapRect, zoomScale: MKZoomScale, in context: CGContext) {
        guard let polygon = overlay as? MKPolygon else { return }

        // Create gradient
        let colorSpace = CGColorSpaceCreateDeviceRGB()
        let cgColors = gradientColors.map { $0.cgColor } as CFArray

        guard let gradient = CGGradient(colorsSpace: colorSpace, colors: cgColors, locations: gradientLocations) else {
            return
        }

        // Draw polygon with gradient
        let path = polygonPath(for: polygon)
        context.addPath(path)
        context.clip()

        let boundingBox = path.boundingBox
        let startPoint = CGPoint(x: boundingBox.minX, y: boundingBox.minY)
        let endPoint = CGPoint(x: boundingBox.maxX, y: boundingBox.maxY)

        context.drawLinearGradient(gradient, start: startPoint, end: endPoint, options: [])
    }

    private func polygonPath(for polygon: MKPolygon) -> CGPath {
        let path = CGMutablePath()
        var points = polygon.points()

        if polygon.pointCount > 0 {
            let firstPoint = point(for: points[0].coordinate)
            path.move(to: firstPoint)

            for i in 1..<polygon.pointCount {
                let point = self.point(for: points[i].coordinate)
                path.addLine(to: point)
            }

            path.closeSubpath()
        }

        return path
    }
}
