## 4. Real-time Location Tracking & Geofencing (60 min)

### Advanced Location Manager
```swift
import CoreLocation

class AdvancedLocationManager: NSObject, ObservableObject {
    private let locationManager = CLLocationManager()

    @Published var currentLocation: CLLocation?
    @Published var authorizationStatus: CLAuthorizationStatus
    @Published var trackedLocations: [CLLocation] = []
    @Published var activeGeofences: [CLCircularRegion] = []

    override init() {
        self.authorizationStatus = locationManager.authorizationStatus
        super.init()

        locationManager.delegate = self
        locationManager.desiredAccuracy = kCLLocationAccuracyBest
        locationManager.distanceFilter = 10.0 // Update every 10 meters
        locationManager.activityType = .fitness
        locationManager.pausesLocationUpdatesAutomatically = false

        #if os(iOS)
        locationManager.showsBackgroundLocationIndicator = true
        locationManager.allowsBackgroundLocationUpdates = true
        #endif
    }

    func startTracking() {
        switch authorizationStatus {
        case .notDetermined:
            locationManager.requestWhenInUseAuthorization()
            locationManager.requestAlwaysAuthorization()

        case .authorizedWhenInUse, .authorizedAlways:
            locationManager.startUpdatingLocation()
            locationManager.startMonitoringSignificantLocationChanges()

        default:
            print("Location access denied")
        }
    }

    func stopTracking() {
        locationManager.stopUpdatingLocation()
        locationManager.stopMonitoringSignificantLocationChanges()
    }

    func addGeofence(center: CLLocationCoordinate2D, radius: CLLocationDistance, identifier: String) {
        let region = CLCircularRegion(center: center, radius: radius, identifier: identifier)
        region.notifyOnEntry = true
        region.notifyOnExit = true

        locationManager.startMonitoring(for: region)
        activeGeofences.append(region)
    }

    func removeGeofence(identifier: String) {
        if let region = activeGeofences.first(where: { $0.identifier == identifier }) {
            locationManager.stopMonitoring(for: region)
            activeGeofences.removeAll { $0.identifier == identifier }
        }
    }
}

// MARK: - CLLocationManagerDelegate

extension AdvancedLocationManager: CLLocationManagerDelegate {
    func locationManager(_ manager: CLLocationManager, didUpdateLocations locations: [CLLocation]) {
        guard let location = locations.last else { return }

        currentLocation = location
        trackedLocations.append(location)

        // Limit tracked locations to prevent memory issues
        if trackedLocations.count > 1000 {
            trackedLocations.removeFirst(500)
        }

        // Post notification for location update
        NotificationCenter.default.post(name: .locationDidUpdate, object: location)
    }

    func locationManager(_ manager: CLLocationManager, didEnterRegion region: CLRegion) {
        guard let circularRegion = region as? CLCircularRegion else { return }

        print("Entered geofence: \(circularRegion.identifier)")
        NotificationCenter.default.post(name: .geofenceEntered, object: circularRegion)
    }

    func locationManager(_ manager: CLLocationManager, didExitRegion region: CLRegion) {
        guard let circularRegion = region as? CLCircularRegion else { return }

        print("Exited geofence: \(circularRegion.identifier)")
        NotificationCenter.default.post(name: .geofenceExited, object: circularRegion)
    }

    func locationManager(_ manager: CLLocationManager, didChangeAuthorization status: CLAuthorizationStatus) {
        authorizationStatus = status

        if status == .authorizedWhenInUse || status == .authorizedAlways {
            startTracking()
        }
    }

    func locationManager(_ manager: CLLocationManager, didFailWithError error: Error) {
        print("Location manager error: \(error.localizedDescription)")
    }
}

// Notifications
 extension Notification.Name {
    static let locationDidUpdate = Notification.Name("locationDidUpdate")
    static let geofenceEntered = Notification.Name("geofenceEntered")
    static let geofenceExited = Notification.Name("geofenceExited")
}
