### Route Tracking & Path Drawing
```swift
class RouteTracker: ObservableObject {
    @Published var currentRoute: MKPolyline?
    @Published var routeDistance: CLLocationDistance = 0.0
    @Published var averageSpeed: CLLocationSpeed = 0.0

    private var locations: [CLLocation] = []
    private var timer: Timer?

    func addLocation(_ location: CLLocation) {
        locations.append(location)
        updateRoute()
        calculateMetrics()
    }

    private func updateRoute() {
        guard locations.count >= 2 else { return }

        var coordinates = locations.map { $0.coordinate }
        currentRoute = MKPolyline(coordinates: &coordinates, count: coordinates.count)
    }

    private func calculateMetrics() {
        // Calculate total distance
        routeDistance = calculateTotalDistance()

        // Calculate average speed
        if let firstLocation = locations.first, let lastLocation = locations.last {
            let timeInterval = lastLocation.timestamp.timeIntervalSince(firstLocation.timestamp)
            if timeInterval > 0 {
                averageSpeed = routeDistance / timeInterval
            }
        }
    }

    private func calculateTotalDistance() -> CLLocationDistance {
        var totalDistance: CLLocationDistance = 0.0

        for i in 1..<locations.count {
            totalDistance += locations[i].distance(from: locations[i - 1])
        }

        return totalDistance
    }

    func clearRoute() {
        locations.removeAll()
        currentRoute = nil
        routeDistance = 0.0
        averageSpeed = 0.0
    }
}

// Route Renderer
class RouteRenderer: MKOverlayRenderer {
    private let routeColor: UIColor
    private let routeWidth: CGFloat

    init(overlay: MKOverlay, color: UIColor = .systemBlue, width: CGFloat = 4.0) {
        self.routeColor = color
        self.routeWidth = width
        super.init(overlay: overlay)
    }

    override func draw(_ mapRect: MKMapRect, zoomScale: MKZoomScale, in context: CGContext) {
        guard let polyline = overlay as? MKPolyline else { return }

        // Configure line properties
        context.setLineWidth(routeWidth / zoomScale)
        context.setLineCap(.round)
        context.setLineJoin(.round)
        context.setStrokeColor(routeColor.cgColor)

        // Create path
        let path = CGMutablePath()
        var points = polyline.points()

        if polyline.pointCount > 0 {
            let firstPoint = point(for: points[0].coordinate)
            path.move(to: firstPoint)

            for i in 1..<polyline.pointCount {
                let point = self.point(for: points[i].coordinate)
                path.addLine(to: point)
            }
        }

        // Draw path
        context.addPath(path)
        context.strokePath()
    }
}
