## 5. Performance Optimization & Best Practices (30 min)

### Memory Management & Performance
```swift
class OptimizedMapViewController: UIViewController, MKMapViewDelegate {
    @IBOutlet weak var mapView: MKMapView!

    private var annotationReuseQueue = Set<MKAnnotation>()
    private var overlayReuseQueue = Set<MKOverlay>()
    private let maxAnnotations = 1000
    private let maxOverlays = 50

    override func viewDidLoad() {
        super.viewDidLoad()
        mapView.delegate = self
        setupPerformanceOptimizations()
    }

    private func setupPerformanceOptimizations() {
        // Enable clustering for better performance with many annotations
        if #available(iOS 11.0, *) {
            mapView.register(CustomAnnotationView.self, forAnnotationViewWithReuseIdentifier: MKMapViewDefaultAnnotationViewReuseIdentifier)
        }

        // Limit visible annotations based on zoom level
        mapView.delegate = self
    }

    func addOptimizedAnnotations(_ annotations: [MKAnnotation]) {
        // Remove old annotations if we exceed the limit
        if mapView.annotations.count + annotations.count > maxAnnotations {
            let oldAnnotations = mapView.annotations.filter { !($0 is MKUserLocation) }
            let annotationsToRemove = Array(oldAnnotations.prefix(annotations.count))
            mapView.removeAnnotations(annotationsToRemove)
        }

        mapView.addAnnotations(annotations)
    }

    func addOptimizedOverlay(_ overlay: MKOverlay) {
        // Remove old overlays if we exceed the limit
        if mapView.overlays.count >= maxOverlays {
            let overlayToRemove = mapView.overlays.first
            if let overlayToRemove = overlayToRemove {
                mapView.removeOverlay(overlayToRemove)
            }
        }

        mapView.addOverlay(overlay)
    }

    // MARK: - MKMapViewDelegate

    func mapView(_ mapView: MKMapView, regionDidChangeAnimated animated: Bool) {
        // Optimize annotations based on current zoom level
        optimizeAnnotationsForCurrentZoom()
    }

    private func optimizeAnnotationsForCurrentZoom() {
        let zoomLevel = calculateZoomLevel()

        // Show fewer annotations at lower zoom levels
        if zoomLevel < 10 {
            // Only show high-priority annotations
            let highPriorityAnnotations = mapView.annotations.filter { annotation in
                if let customAnnotation = annotation as? CustomPointAnnotation {
                    return customAnnotation.priority >= 2
                }
                return false
            }

            let lowPriorityAnnotations = mapView.annotations.filter { annotation in
                if let customAnnotation = annotation as? CustomPointAnnotation {
                    return customAnnotation.priority < 2
                }
                return false
            }

            mapView.removeAnnotations(lowPriorityAnnotations)
        } else {
            // Show all annotations at higher zoom levels
            // Re-add any previously removed annotations
        }
    }

    private func calculateZoomLevel() -> Double {
        let zoomScale = mapView.visibleMapRect.size.width / Double(mapView.bounds.size.width)
        let zoomExponent = log2(zoomScale)
        return 20.0 - zoomExponent
    }
}
