        self.title = title
        self.subtitle = subtitle
        self.category = category
        self.priority = priority
        self.metadata = metadata
        self.lastUpdated = Date()
    }

    enum Category: String, CaseIterable {
        case restaurant, hotel, attraction, transportation, emergency, custom
    }

    enum Priority: Int {
        case low = 1, medium, high, critical
    }
}

class SmartAnnotationView: MKMarkerAnnotationView {
    private var pulseLayer: CALayer?

    override var annotation: MKAnnotation? {
        didSet {
            configureView()
        }
    }

    private func configureView() {
        guard let smartAnnotation = annotation as? SmartAnnotation else { return }

        // Configure based on category and priority
        configureAppearance(for: smartAnnotation.category, priority: smartAnnotation.priority)

        // Add gestures
        addInteraction(UIDropInteraction(delegate: self))

        // Add pulse animation for high priority
        if smartAnnotation.priority == .critical {
            addPulseAnimation()
        }

        canShowCallout = true
        configureCalloutAccessories()
    }

    private func configureAppearance(for category: SmartAnnotation.Category, priority: SmartAnnotation.Priority) {
        switch category {
        case .restaurant:
            markerTintColor = .systemGreen
            glyphImage = UIImage(systemName: "fork.knife")
        case .hotel:
            markerTintColor = .systemBlue
            glyphImage = UIImage(systemName: "bed.double")
        case .attraction:
            markerTintColor = .systemOrange
            glyphImage = UIImage(systemName: "star")
        case .transportation:
            markerTintColor = .systemPurple
            glyphImage = UIImage(systemName: "bus")
        case .emergency:
            markerTintColor = .systemRed
            glyphImage = UIImage(systemName: "cross")
        case .custom:
            markerTintColor = .systemGray
            glyphImage = UIImage(systemName: "pin")
        }

        // Priority styling
        switch priority {
        case .low:
            alpha = 0.7
        case .medium:
            alpha = 1.0
        case .high:
            glyphTintColor = .systemYellow
        case .critical:
            glyphTintColor = .systemRed
            markerTintColor = .systemRed
        }
    }

    private func addPulseAnimation() {
        let pulse = CABasicAnimation(keyPath: "transform.scale")
        pulse.duration = 1.0
        pulse.fromValue = 1.0
        pulse.toValue = 1.2
        pulse.autoreverses = true
        pulse.repeatCount = .infinity

        layer.add(pulse, forKey: "pulse")
    }

    private func configureCalloutAccessories() {
        let infoButton = UIButton(type: .detailDisclosure)
        infoButton.tintColor = .systemBlue
        rightCalloutAccessoryView = infoButton

        let imageView = UIImageView(image: UIImage(systemName: "info.circle"))
        imageView.tintColor = .systemGray
        leftCalloutAccessoryView = imageView
    }
}

// === PART 2: Complex Overlays & Visualization ===
class HeatMapOverlay: MKPolygon {
    var intensity: Double
    var gradientStops: [GradientStop]

    init(coordinates: UnsafePointer<CLLocationCoordinate2D>, count: Int, 
         intensity: Double, gradientStops: [GradientStop]) {
        self.intensity = intensity
        self.gradientStops = gradientStops
        super.init(coordinates: coordinates, count: count)
    }

    struct GradientStop {
        let color: UIColor
        let location: CGFloat
    }
}

class HeatMapRenderer: MKOverlayRenderer {
    override func draw(_ mapRect: MKMapRect, zoomScale: MKZoomScale, in context: CGContext) {
        guard let heatMap = overlay as? HeatMapOverlay else { return }

        let path = polygonPath(for: heatMap)
        context.addPath(path)
        context.clip()

        // Create gradient based on intensity
        let colors = heatMap.gradientStops.map { $0.color.cgColor }
        let locations = heatMap.gradientStops.map { $0.location }

        let colorSpace = CGColorSpaceCreateDeviceRGB()
        guard let gradient = CGGradient(colorsSpace: colorSpace, colors: colors as CFArray, locations: locations) else {
            return
        }

        let boundingBox = path.boundingBox
        let startPoint = CGPoint(x: boundingBox.minX, y: boundingBox.minY)
        let endPoint = CGPoint(x: boundingBox.maxX, y: boundingBox.maxY)

        // Adjust gradient based on heat intensity
        let intensityFactor = CGFloat(heatMap.intensity)
        let adjustedEndPoint = CGPoint(
            x: startPoint.x + (endPoint.x - startPoint.x) * intensityFactor,
            y: startPoint.y + (endPoint.y - startPoint.y) * intensityFactor
        )

        context.drawLinearGradient(gradient, start: startPoint, end: adjustedEndPoint, options: [])
    }
}

// === PART 3: Real-time Location & Geofencing ===
class AdvancedGeofenceManager: NSObject, ObservableObject {
    private let locationManager = CLLocationManager()

    @Published var activeGeofences: [SmartGeofence] = []
    @Published var currentGeofenceEvents: [GeofenceEvent] = []

    override init() {
        super.init()
        locationManager.delegate = self
        locationManager.desiredAccuracy = kCLLocationAccuracyBest
    }

    func addGeofence(_ geofence: SmartGeofence) {
        let region = CLCircularRegion(center: geofence.center, radius: geofence.radius, identifier: geofence.id)
        region.notifyOnEntry = geofence.notifyOnEntry
        region.notifyOnExit = geofence.notifyOnExit

        locationManager.startMonitoring(for: region)
        activeGeofences.append(geofence)
    }

    func removeGeofence(withId id: String) {
        if let geofence = activeGeofences.first(where: { $0.id == id }) {
            let region = CLCircularRegion(center: geofence.center, radius: geofence.radius, identifier: geofence.id)
            locationManager.stopMonitoring(for: region)
            activeGeofences.removeAll { $0.id == id }
        }
    }
}

struct SmartGeofence {
    let id: String
    let center: CLLocationCoordinate2D
    let radius: CLLocationDistance
    let name: String
    let notifyOnEntry: Bool
    let notifyOnExit: Bool
    let autoRemove: Bool
}

struct GeofenceEvent {
    let geofenceId: String
    let type: EventType
    let timestamp: Date
    let location: CLLocation

    enum EventType {
        case entry, exit
    }
}

