// === PART 5: Advanced Features & Integration ===
class MapSearchManager: ObservableObject {
    @Published var searchResults: [SmartAnnotation] = []
    @Published var searchQuery: String = ""

    private var searchCompleter = MKLocalSearchCompleter()
    private var cancellables = Set<AnyCancellable>()

    init() {
        setupSearchCompleter()
        setupSearchBinding()
    }

    private func setupSearchCompleter() {
        searchCompleter.resultTypes = .pointOfInterest
        searchCompleter.delegate = self
    }

    private func setupSearchBinding() {
        $searchQuery
            .debounce(for: .milliseconds(300), scheduler: RunLoop.main)
            .removeDuplicates()
            .sink { [weak self] query in
                self?.searchCompleter.queryFragment = query
            }
            .store(in: &cancellables)
    }

    func performSearch(for completion: MKLocalSearchCompletion) {
        let request = MKLocalSearch.Request(completion: completion)
        let search = MKLocalSearch(request: request)

        search.start { [weak self] response, error in
            guard let self = self, let response = response else { return }

            self.searchResults = response.mapItems.compactMap { item in
                guard let location = item.placemark.location else { return nil }

                return SmartAnnotation(
                    coordinate: location.coordinate,
                    title: item.name,
                    subtitle: item.placemark.title,
                    category: .custom,
                    priority: .medium,
                    metadata: ["mapItem": item]
                )
            }
        }
    }
}

