// MARK: - CLLocationManagerDelegate

extension AdvancedGeofenceManager: CLLocationManagerDelegate {
    func locationManager(_ manager: CLLocationManager, didEnterRegion region: CLRegion) {
        guard let circularRegion = region as? CLCircularRegion else { return }

        let event = GeofenceEvent(
            geofenceId: circularRegion.identifier,
            type: .entry,
            timestamp: Date(),
            location: CLLocation(latitude: circularRegion.center.latitude, longitude: circularRegion.center.longitude)
        )

        currentGeofenceEvents.append(event)

        // Keep only last 100 events
        if currentGeofenceEvents.count > 100 {
            currentGeofenceEvents.removeFirst(50)
        }
    }

    func locationManager(_ manager: CLLocationManager, didExitRegion region: CLRegion) {
        guard let circularRegion = region as? CLCircularRegion else { return }

        let event = GeofenceEvent(
            geofenceId: circularRegion.identifier,
            type: .exit,
            timestamp: Date(),
            location: CLLocation(latitude: circularRegion.center.latitude, longitude: circularRegion.center.longitude)
        )

        currentGeofenceEvents.append(event)

        // Auto-remove geofence if configured
        if let geofence = activeGeofences.first(where: { $0.id == circularRegion.identifier && $0.autoRemove }) {
            removeGeofence(withId: geofence.id)
        }
    }
}
